  public static int getFloor(String str){
    int numFloors = 0;
    for (int i = 0; i < str.length(); i++){
      if (str.charAt(i) == '('){
        numFloors += 1;
      }else if (str.charAt(i) == ')'){
        numFloors -= 1;
      }
    }
    return numFloors;
  }
